var (
	ErrNotOSCPU            = errors.New("not contain cpu")
	ErrOSCPUParseErrNoInfo = errors.New("stat does not contain cpu info")
)

func parseCPUInfo(buf []byte) (total uint64, used uint64, err error) {
	fields := bytes.Fields(bytes.TrimRight(buf, "\n"))
	if len(fields) == 0 || len(fields) < 7 {
		return 0, 0, ErrOSCPUParseErrNoInfo
	}

	user, err := utils.ParseUint(fields[1])
	if err != nil {
		return 0, 0, err
	}
	nice, err := utils.ParseUint(fields[2])
	if err != nil {
		return 0, 0, err
	}
	system, err := utils.ParseUint(fields[3])
	if err != nil {
		return 0, 0, err
	}
	idle, err := utils.ParseUint(fields[4])
	if err != nil {
		return 0, 0, err
	}
	iowait, err := utils.ParseUint(fields[5])
	if err != nil {
		return 0, 0, err
	}
	irq, err := utils.ParseUint(fields[6])
	if err != nil {
		return 0, 0, err
	}
	softirq, err := utils.ParseUint(fields[7])
	if err != nil {
		return 0, 0, err
	}

	var steal, guest, guestNice uint64

	if len(fields) > 8 { // Linux >= 2.6.11
		steal, err = utils.ParseUint(fields[8])
		if err != nil {
			return 0, 0, err
		}
	}
	if len(fields) > 9 { // Linux >= 2.6.24
		guest, err = utils.ParseUint(fields[9])
		if err != nil {
			return 0, 0, err
		}
	}
	if len(fields) > 10 { // Linux >= 3.2.0
		guestNice, err = utils.ParseUint(fields[10])
		if err != nil {
			return 0, 0, err
		}
	}

	total = user + system + nice + irq +
		softirq + steal + idle + iowait +
		guest + guestNice
	used = total - idle - iowait

	return total, used, nil
}

func getCPUInfo(f io.ReadSeeker) (total uint64, used uint64, err error) {
	_, err = f.Seek(0, 0)
	if err != nil {
		return 0, 0, err
	}

	scanner := bufio.NewScanner(f)
	scanner.Split(bufio.ScanLines)

	for scanner.Scan() {
		buf := scanner.Bytes()
		if bytes.HasPrefix(buf, []byte("cpu ")) {
			return parseCPUInfo(buf)
		}
	}

	if err := scanner.Err(); err != nil {
		return 0, 0, err
	}

	return 0, 0, ErrNotOSCPU
}
